        _nonReentrantBefore();
        _;
        _nonReentrantAfter();
    }

    function _nonReentrantBefore() private {
        // On the first call to nonReentrant, _status will be NOT_ENTERED
        if (_status == ENTERED) {
            revert ReentrancyGuardReentrantCall();
        }

        // Any calls to nonReentrant after this point will fail
        _status = ENTERED;
    }

    function _nonReentrantAfter() private {
        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = NOT_ENTERED;
    }

    /**
     * @dev Returns true if the reentrancy guard is currently set to "entered", which indicates there is a
     * `nonReentrant` function in the call stack.
     */
    function _reentrancyGuardEntered() internal view returns (bool) {
        return _status == ENTERED;
    }
}

contracts/BasketFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./BasketRegistry.sol";
import "./BasketVault.sol";
import "./EquiBasketToken.sol";

/**
 * @title BasketFactory
 * @notice Factory for creating complete basket setups in a single transaction
 * @dev This factory handles the complete lifecycle of basket creation:
 *      1. Creates the basket in BasketRegistry
 *      2. Deploys an EquiBasketToken for the basket
 *      3. Sets the vault as the token minter
 *      4. Registers the token in the BasketVault
 * 
 *      This ensures users never encounter "BasketTokenNotRegistered" errors
 *      because the token is always created and registered atomically.
 */
contract BasketFactory is Ownable {
    // ============================================================
    // ========================== STATE ===========================
    // ============================================================

    /// @notice Reference to the BasketRegistry
    BasketRegistry public immutable basketRegistry;

    /// @notice Reference to the BasketVault
    BasketVault public basketVault;

    /// @notice Mapping from basketId to its token address
    mapping(uint256 => address) public basketTokens;

    // ============================================================
    // ========================= EVENTS ===========================
    // ============================================================

    event BasketCreatedWithToken(
        uint256 indexed basketId,
        address indexed creator,
        address tokenAddress,
        string name,
        string symbol
    );

    event VaultUpdated(address indexed oldVault, address indexed newVault);

    // ============================================================
    // ========================= ERRORS ===========================
    // ============================================================

    error VaultNotSet();
    error ZeroAddress();

    // ============================================================
    // ====================== CONSTRUCTOR =========================
    // ============================================================

    /**
     * @notice Deploy the factory with required dependencies
     * @param _basketRegistry Address of the BasketRegistry
     * @param _basketVault Address of the BasketVault (can be set later)
     */
    constructor(
        address _basketRegistry,
        address _basketVault
    ) Ownable(msg.sender) {
        if (_basketRegistry == address(0)) revert ZeroAddress();

        basketRegistry = BasketRegistry(_basketRegistry);

        if (_basketVault != address(0)) {
            basketVault = BasketVault(payable(_basketVault));
        }
    }

    // ============================================================
    // =================== ADMIN FUNCTIONS ========================
    // ============================================================

    /**
     * @notice Set or update the vault address
     * @param _vault Address of the BasketVault
     */
    function setVault(address _vault) external onlyOwner {
        if (_vault == address(0)) revert ZeroAddress();

        address oldVault = address(basketVault);
        basketVault = BasketVault(payable(_vault));

        emit VaultUpdated(oldVault, _vault);
    }

    // ============================================================
    // ================= FACTORY FUNCTIONS ========================
    // ============================================================

    /**
     * @notice Create a complete basket with token in a single transaction
     * @dev This function:
     *      1. Creates the basket in registry
     *      2. Deploys a new EquiBasketToken
     *      3. Sets vault as the token minter
     *      4. Registers token in vault
     * 
     * @param assets Array of asset identifiers (e.g., ["AAPL", "NVDA"])
     * @param weights Array of weights in basis points (must sum to 10000)
     * @param name Human-readable name for the basket
     * @param symbol Token symbol for the basket
     * @return basketId The unique ID assigned to the new basket
     * @return tokenAddress The address of the deployed EquiBasketToken
     */
    function createBasketWithToken(
        string[] calldata assets,
        uint256[] calldata weights,
        string calldata name,
        string calldata symbol
    ) external returns (uint256 basketId, address tokenAddress) {
        if (address(basketVault) == address(0)) revert VaultNotSet();

        // Step 1: Create basket in registry
        basketId = basketRegistry.createBasket(assets, weights, name, symbol);

        // Step 2: Deploy EquiBasketToken
        EquiBasketToken token = new EquiBasketToken(
            basketId,
            name,
            symbol,
            address(this) // Factory is initial owner
        );
        tokenAddress = address(token);

        // Store for reference
        basketTokens[basketId] = tokenAddress;

        // Step 3: Set vault as minter in token
        token.setVault(address(basketVault));

        // Step 4: Register token in vault
        basketVault.registerBasketToken(basketId, tokenAddress);

        // Step 5: Transfer token ownership to the basket creator
        token.transferOwnership(msg.sender);

        emit BasketCreatedWithToken(basketId, msg.sender, tokenAddress, name, symbol);
    }

    // ============================================================
    // ===================== VIEW FUNCTIONS =======================
    // ============================================================

    /**
     * @notice Get the token address for a basket
     * @param basketId The basket ID
     * @return The token address (zero if not created via factory)
     */
    function getBasketToken(uint256 basketId) external view returns (address) {
        return basketTokens[basketId];
    }
}

contracts/BasketOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./BasketRegistry.sol";

/**
 * @title BasketOracle
 * @notice Oracle for computing basket prices from individual asset prices
 * @dev This is the CORE INNOVATION of EquiBaskets.
 *      Unlike single-asset oracles that just return a price, this oracle
 *      COMPUTES FINANCIAL MEANING by:
 *      1. Reading basket composition from BasketRegistry
 *      2. Fetching price for each underlying asset
 *      3. Computing weighted average basket price
 * 
 *      For the hackathon, asset prices are hardcoded on-chain in USD format.
 *      This module is intentionally designed to be replaced later with a
 *      real oracle integration (e.g., Pyth, Chainlink).
 * 
 *      All prices are stored and returned in 1e18 format.
 *      For example, $100 = 100 * 1e18 = 100000000000000000000
 * 
 *      The MNT/USD price is also stored here for collateral valuation.
 */
contract BasketOracle is Ownable {
    // ============================================================
    // ========================== STATE ===========================
    // ============================================================

    /// @notice Reference to the BasketRegistry for reading composition
    BasketRegistry public immutable basketRegistry;

    /// @notice Mapping from asset identifier to USD price (1e18 format)
    mapping(string => uint256) public assetPrices;

    /// @notice List of all registered asset identifiers
    string[] public registeredAssets;

    /// @notice Mapping to check if an asset is registered
    mapping(string => bool) public isAssetRegistered;

    /// @notice MNT/USD price in 1e18 format
    /// @dev Used for calculating collateral value in the vault
    uint256 public mntUsdPrice;

    // ============================================================
    // ========================= EVENTS ===========================
    // ============================================================

    /// @notice Emitted when an asset price is updated
    event AssetPriceUpdated(string indexed assetId, uint256 oldPrice, uint256 newPrice);

    /// @notice Emitted when MNT/USD price is updated
    event MntPriceUpdated(uint256 oldPrice, uint256 newPrice);

    /// @notice Emitted when a new asset is registered
    event AssetRegistered(string assetId, uint256 initialPrice);

    // ============================================================
    // ========================= ERRORS ===========================
    // ============================================================

    /// @notice Thrown when basket doesn't exist
    error BasketDoesNotExist();

    /// @notice Thrown when asset price is not available
    error AssetPriceNotAvailable(string assetId);

    /// @notice Thrown when MNT price is not set
    error MntPriceNotSet();

    /// @notice Thrown when price is zero
    error ZeroPrice();

    /// @notice Thrown when asset is already registered
    error AssetAlreadyRegistered(string assetId);

    // ============================================================
    // ====================== CONSTRUCTOR =========================
    // ============================================================

    /**
     * @notice Initialize the BasketOracle with a registry reference
     * @param _basketRegistry Address of the BasketRegistry contract
     */
    constructor(address _basketRegistry) Ownable(msg.sender) {
        basketRegistry = BasketRegistry(_basketRegistry);

        // Initialize with default prices for common assets (in 1e18 format)
        // These represent example assets that might be in baskets
        _registerAssetWithPrice("AAPL", 175 * 1e18);    // Apple Inc. at $175
        _registerAssetWithPrice("NVDA", 490 * 1e18);    // NVIDIA at $490
        _registerAssetWithPrice("GOLD", 2050 * 1e18);   // Gold per oz at $2050
        _registerAssetWithPrice("SILVER", 24 * 1e18);   // Silver per oz at $24
        _registerAssetWithPrice("HITACHI", 92 * 1e18);  // Hitachi at $92
        _registerAssetWithPrice("MSFT", 380 * 1e18);    // Microsoft at $380
        _registerAssetWithPrice("GOOGL", 140 * 1e18);   // Alphabet at $140
        _registerAssetWithPrice("AMZN", 155 * 1e18);    // Amazon at $155
        _registerAssetWithPrice("TSLA", 250 * 1e18);    // Tesla at $250
        _registerAssetWithPrice("BTC", 42000 * 1e18);   // Bitcoin at $42000

        // Initialize MNT price (example: $0.50)
        mntUsdPrice = 5 * 1e17; // 0.5 * 1e18
    }

    // ============================================================
    // ================== INTERNAL FUNCTIONS ======================
    // ============================================================

    /**
     * @notice Internal function to register an asset with initial price
     * @param assetId Asset identifier
     * @param price Initial price in 1e18 format
     */
    function _registerAssetWithPrice(string memory assetId, uint256 price) internal {
        assetPrices[assetId] = price;
        registeredAssets.push(assetId);
        isAssetRegistered[assetId] = true;
    }

    // ============================================================
    // =================== ADMIN FUNCTIONS ========================
    // ============================================================

    /**
     * @notice Register a new asset with initial price
     * @param assetId Asset identifier (e.g., "AAPL", "GOLD")
     * @param price Initial price in 1e18 format
     */
    function registerAsset(string calldata assetId, uint256 price) external onlyOwner {
        if (isAssetRegistered[assetId]) revert AssetAlreadyRegistered(assetId);
        if (price == 0) revert ZeroPrice();

        _registerAssetWithPrice(assetId, price);

        emit AssetRegistered(assetId, price);
    }

    /**
     * @notice Update the price of an asset
     * @param assetId Asset identifier
     * @param newPrice New price in 1e18 format
     */
    function setAssetPrice(string calldata assetId, uint256 newPrice) external onlyOwner {
        if (newPrice == 0) revert ZeroPrice();

        uint256 oldPrice = assetPrices[assetId];
        assetPrices[assetId] = newPrice;

        // Auto-register if not already registered
        if (!isAssetRegistered[assetId]) {
            registeredAssets.push(assetId);
            isAssetRegistered[assetId] = true;
            emit AssetRegistered(assetId, newPrice);
        }

        emit AssetPriceUpdated(assetId, oldPrice, newPrice);
    }

    /**
     * @notice Update MNT/USD price
     * @param newPrice New MNT/USD price in 1e18 format
     */
    function setMntPrice(uint256 newPrice) external onlyOwner {
        if (newPrice == 0) revert ZeroPrice();

        uint256 oldPrice = mntUsdPrice;
        mntUsdPrice = newPrice;

        emit MntPriceUpdated(oldPrice, newPrice);
    }

    /**
     * @notice Batch update multiple asset prices
     * @param assetIds Array of asset identifiers
     * @param prices Array of new prices in 1e18 format
     */
    function batchSetAssetPrices(
        string[] calldata assetIds,
        uint256[] calldata prices
    ) external onlyOwner {
        require(assetIds.length == prices.length, "Array length mismatch");

        for (uint256 i = 0; i < assetIds.length; i++) {
            if (prices[i] == 0) revert ZeroPrice();

            uint256 oldPrice = assetPrices[assetIds[i]];
            assetPrices[assetIds[i]] = prices[i];

            if (!isAssetRegistered[assetIds[i]]) {
                registeredAssets.push(assetIds[i]);
                isAssetRegistered[assetIds[i]] = true;
                emit AssetRegistered(assetIds[i], prices[i]);
            }

            emit AssetPriceUpdated(assetIds[i], oldPrice, prices[i]);
        }
    }

    // ============================================================
    // ================= CORE PRICING FUNCTIONS ===================
    // ============================================================

    /**
     * @notice Get the weighted price of a basket
     * @dev This is the CORE function of EquiBaskets oracle.
     *      It computes the basket price by:
     *      1. Reading composition from BasketRegistry
     *      2. Fetching price for each asset
     *      3. Computing weighted average: sum(price[i] * weight[i]) / 10000
     * @param basketId The basket ID to get price for
     * @return price Basket price in 1e18 USD format
     */
    function getBasketPrice(uint256 basketId) external view returns (uint256 price) {
        // Verify basket exists
        if (!basketRegistry.basketExists(basketId)) revert BasketDoesNotExist();

        // Get basket composition
        (string[] memory assets, uint256[] memory weights) = basketRegistry.getBasketComposition(basketId);

        // Calculate weighted price
        // Sum of (asset_price * weight) / 10000
        // Using 1e18 precision throughout
        uint256 weightedSum = 0;

        for (uint256 i = 0; i < assets.length; i++) {
            uint256 assetPrice = assetPrices[assets[i]];
            if (assetPrice == 0) revert AssetPriceNotAvailable(assets[i]);

            // Accumulate weighted price
            // assetPrice is in 1e18, weight is in basis points (0-10000)
            weightedSum += assetPrice * weights[i];
        }

        // Divide by 10000 to get final price in 1e18 format
        price = weightedSum / 10000;
    }

    /**
     * @notice Get individual asset price
     * @param assetId Asset identifier
     * @return price Asset price in 1e18 USD format
     */
    function getAssetPrice(string calldata assetId) external view returns (uint256 price) {
        price = assetPrices[assetId];
        if (price == 0) revert AssetPriceNotAvailable(assetId);
    }

    /**
     * @notice Get MNT to USD value
     * @param mntAmount Amount of MNT tokens (in 1e18)
     * @return usdValue USD value in 1e18 format
     */
    function getMntValue(uint256 mntAmount) external view returns (uint256 usdValue) {
        if (mntUsdPrice == 0) revert MntPriceNotSet();
        // Both mntAmount and mntUsdPrice are in 1e18
        // Result needs to be in 1e18, so divide by 1e18
        usdValue = (mntAmount * mntUsdPrice) / 1e18;
    }

    /**
     * @notice Get the MNT amount equivalent to a USD value
     * @param usdValue USD value in 1e18 format
     * @return mntAmount MNT amount in 1e18 format
     */
    function getMntFromUsdValue(uint256 usdValue) external view returns (uint256 mntAmount) {
        if (mntUsdPrice == 0) revert MntPriceNotSet();
        // Inverse of getMntValue
        mntAmount = (usdValue * 1e18) / mntUsdPrice;
    }

    // ============================================================
    // ==================== VIEW FUNCTIONS ========================
    // ============================================================

    /**
     * @notice Get all registered asset identifiers
     * @return Array of asset identifier strings
     */
    function getAllRegisteredAssets() external view returns (string[] memory) {
        return registeredAssets;
    }

    /**
     * @notice Get the number of registered assets
     * @return Count of registered assets
     */
    function getRegisteredAssetCount() external view returns (uint256) {
        return registeredAssets.length;
    }

    /**
     * @notice Check if a basket has all required asset prices
     * @param basketId The basket ID to check
     * @return valid True if all asset prices are available
     * @return missingAsset The first missing asset (if any)
     */
    function validateBasketPrices(uint256 basketId) external view returns (
        bool valid,
        string memory missingAsset
    ) {
        if (!basketRegistry.basketExists(basketId)) revert BasketDoesNotExist();

        (string[] memory assets, ) = basketRegistry.getBasketComposition(basketId);

        for (uint256 i = 0; i < assets.length; i++) {
            if (assetPrices[assets[i]] == 0) {
                return (false, assets[i]);
            }
        }

        return (true, "");
    }
}

contracts/BasketRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title BasketRegistry
 * @notice Registry for basket-based synthetic assets
 * @dev This contract is the canonical on-chain source of truth for basket composition.
 *      It stores basket metadata including:
 *      - Asset composition (list of asset identifiers)
 *      - Weights (in basis points, must sum to 10000)
 *      - Creator address (fund creator role)
 *      - Activation state
 * 
 *      IMPORTANT: This contract is pure metadata storage.
 *      No pricing logic should exist here - that belongs in BasketOracle.
 * 
 *      The basketId is a unique identifier assigned sequentially to each basket.
 *      Asset identifiers are strings (e.g., "AAPL", "GOLD", "NVDA") that are used
 *      by the BasketOracle to look up prices.
 */
contract BasketRegistry is Ownable {
    // ============================================================
    // ====================== DATA STRUCTURES =====================
    // ============================================================

    /**
     * @notice Represents a basket of synthetic assets
     * @param creator Address of the fund creator who created this basket
     * @param assets List of asset identifiers (e.g., ["AAPL", "NVDA", "GOLD"])
     * @param weights Weight of each asset in basis points (must sum to 10000)
     * @param active Whether this basket is active for minting/trading
     * @param name Human-readable name for the basket
     * @param symbol Token symbol for the basket token
     */
    struct Basket {
        address creator;
        string[] assets;
        uint256[] weights;
        bool active;
        string name;
        string symbol;
    }

    // ============================================================
    // ========================== STATE ===========================
    // ============================================================

    /// @notice Counter for generating unique basket IDs
    uint256 public basketCount;

    /// @notice Mapping from basket ID to basket data
    mapping(uint256 => Basket) private baskets;

    /// @notice Mapping from basket creator to list of their basket IDs
    mapping(address => uint256[]) public creatorBaskets;

    // ============================================================
    // ========================= EVENTS ===========================
    // ============================================================

    /// @notice Emitted when a new basket is created
    /// @dev Full basket details can be queried via getBasket(basketId)
    event BasketCreated(
        uint256 indexed basketId,
        address indexed creator,
        uint256 assetCount
    );

    /// @notice Emitted when a basket's active state changes
    event BasketActiveStateChanged(uint256 indexed basketId, bool active);

    // ============================================================
    // ========================= ERRORS ===========================
    // ============================================================

    /// @notice Thrown when assets and weights arrays have different lengths
    error ArrayLengthMismatch();

    /// @notice Thrown when assets array is empty
    error EmptyAssetsArray();

    /// @notice Thrown when weights don't sum to 10000 basis points
    error InvalidWeightsSum();

    /// @notice Thrown when a weight is zero
    error ZeroWeight();

    /// @notice Thrown when basket doesn't exist
    error BasketDoesNotExist();

    /// @notice Thrown when caller is not the basket creator
    error NotBasketCreator();

    // ============================================================
    // ====================== CONSTRUCTOR =========================
    // ============================================================

    constructor() Ownable(msg.sender) {
        // BasketRegistry starts with basketCount = 0
        // First basket created will have basketId = 1
    }

    // ============================================================
    // =================== EXTERNAL FUNCTIONS =====================
    // ============================================================

    /**
     * @notice Create a new basket with specified assets and weights
     * @dev Only callable by anyone (fund creator role).
     *      Weights must sum to 10000 basis points (100%).
     *      Each weight must be non-zero.
     * @param assets Array of asset identifiers
     * @param weights Array of weights in basis points
     * @param name Human-readable name for the basket
     * @param symbol Token symbol for the basket
     * @return basketId The unique ID assigned to the new basket
     */
    function createBasket(
        string[] calldata assets,
        uint256[] calldata weights,
        string calldata name,
        string calldata symbol
    ) external returns (uint256 basketId) {
        // Validate inputs
        uint256 assetLen = assets.length;
        if (assetLen == 0) revert EmptyAssetsArray();
        if (assetLen != weights.length) revert ArrayLengthMismatch();

        // Validate weights sum to 10000 basis points
        uint256 totalWeight = 0;
        for (uint256 i = 0; i < assetLen;) {
            if (weights[i] == 0) revert ZeroWeight();
            totalWeight += weights[i];
            unchecked { ++i; }
        }
        if (totalWeight != 10000) revert InvalidWeightsSum();

        // Generate new basket ID (starts from 1)
        basketId = ++basketCount;

        // Create the basket - store directly to avoid extra local vars
        Basket storage b = baskets[basketId];
        b.creator = msg.sender;
        b.active = true;
        b.name = name;
        b.symbol = symbol;

        // Copy assets and weights to storage
        for (uint256 i = 0; i < assetLen;) {
            b.assets.push(assets[i]);
            b.weights.push(weights[i]);
            unchecked { ++i; }
        }

        // Track creator's baskets
        creatorBaskets[msg.sender].push(basketId);

        emit BasketCreated(basketId, msg.sender, assetLen);
    }

    /**
     * @notice Deactivate a basket (only creator or owner)
     * @param basketId The basket to deactivate
     */
    function deactivateBasket(uint256 basketId) external {
        Basket storage basket = baskets[basketId];
        if (basket.creator == address(0)) revert BasketDoesNotExist();
        if (msg.sender != basket.creator && msg.sender != owner()) {
            revert NotBasketCreator();
        }

        basket.active = false;
        emit BasketActiveStateChanged(basketId, false);
    }

    /**
     * @notice Reactivate a basket (only creator or owner)
     * @param basketId The basket to reactivate
     */
    function reactivateBasket(uint256 basketId) external {
        Basket storage basket = baskets[basketId];
        if (basket.creator == address(0)) revert BasketDoesNotExist();
        if (msg.sender != basket.creator && msg.sender != owner()) {
            revert NotBasketCreator();
        }

        basket.active = true;
        emit BasketActiveStateChanged(basketId, true);
    }

    // ============================================================
    // ==================== VIEW FUNCTIONS ========================
    // ============================================================

    /**
     * @notice Get complete basket data by ID
     * @param basketId The basket ID to query
     * @return creator The creator address
     * @return assets Array of asset identifiers
     * @return weights Array of weights in basis points
     * @return active Whether the basket is active
     * @return name Basket name
     * @return symbol Basket symbol
     */
    function getBasket(uint256 basketId) external view returns (
        address creator,
        string[] memory assets,
        uint256[] memory weights,
        bool active,
        string memory name,
        string memory symbol
    ) {
        Basket storage basket = baskets[basketId];
        if (basket.creator == address(0)) revert BasketDoesNotExist();

        return (
            basket.creator,
            basket.assets,
            basket.weights,
            basket.active,
            basket.name,
            basket.symbol
        );
    }

    /**
     * @notice Check if a basket is active
     * @param basketId The basket ID to check
     * @return True if active, false otherwise
     */
    function isBasketActive(uint256 basketId) external view returns (bool) {
        Basket storage basket = baskets[basketId];
        if (basket.creator == address(0)) revert BasketDoesNotExist();
        return basket.active;
    }

    /**
     * @notice Get basket composition (assets and weights only)
     * @param basketId The basket ID to query
     * @return assets Array of asset identifiers
     * @return weights Array of weights in basis points
     */
    function getBasketComposition(uint256 basketId) external view returns (
        string[] memory assets,
        uint256[] memory weights
    ) {
        Basket storage basket = baskets[basketId];
        if (basket.creator == address(0)) revert BasketDoesNotExist();
        return (basket.assets, basket.weights);
    }

    /**
     * @notice Get the number of assets in a basket
     * @param basketId The basket ID to query
     * @return Number of assets
     */
    function getBasketAssetCount(uint256 basketId) external view returns (uint256) {
        Basket storage basket = baskets[basketId];
        if (basket.creator == address(0)) revert BasketDoesNotExist();
        return basket.assets.length;
    }

    /**
     * @notice Get all basket IDs created by a specific address
     * @param creator The creator address to query
     * @return Array of basket IDs
     */
    function getBasketsByCreator(address creator) external view returns (uint256[] memory) {
        return creatorBaskets[creator];
    }

    /**
     * @notice Check if a basket exists
     * @param basketId The basket ID to check
     * @return True if exists, false otherwise
     */
    function basketExists(uint256 basketId) external view returns (bool) {
        return baskets[basketId].creator != address(0);
    }
}

contracts/BasketVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./BasketRegistry.sol";
import "./BasketOracle.sol";
import "./EquiBasketToken.sol";

/**
 * @title BasketVault
 * @notice Vault for managing basket-scoped collateral positions using NATIVE MNT
 * @dev This version uses native MNT (msg.value) instead of ERC20 MockMNT
 *      On Mantle, MNT is the native gas token similar to ETH on Ethereum.
 *      
 *      Key features:
 *      - State is (user → basketId → position) scoped
 *      - A user can have multiple baskets with different risk profiles
 *      - Each (user, basketId) pair is an independent position
 *      - Uses NATIVE MNT (msg.value) as collateral
 * 
 *      COLLATERAL FLOW:
 *      1. User deposits native MNT collateral via msg.value
 *      2. User can mint basket tokens if they have sufficient collateral
 *      3. When minting, debt is created and tracked per (user, basketId)
 *      4. When burning, debt is repaid and collateral is freed
 */
contract BasketVault is Ownable, ReentrancyGuard {
    // ============================================================
    // ======================= CONSTANTS ==========================
    // ============================================================

    /// @notice Required collateral ratio for minting (500%)
    uint256 public constant COLLATERAL_RATIO = 500;

    /// @notice Liquidation threshold (150%)
    uint256 public constant LIQUIDATION_THRESHOLD = 150;

    /// @notice Decimal precision for calculations (1e18)
    uint256 public constant PRECISION = 1e18;

    /// @notice Liquidation penalty in basis points (10% = 1000 bp)
    uint256 public constant LIQUIDATION_PENALTY = 1000;

    // ============================================================
    // ========================== STATE ===========================
    // ============================================================

    /// @notice Reference to the basket oracle for pricing
    BasketOracle public basketOracle;

    /// @notice Reference to the basket registry
    BasketRegistry public immutable basketRegistry;

    /// @notice Mapping from basketId to its token contract
    mapping(uint256 => EquiBasketToken) public basketTokens;

    /// @notice User collateral per basket: user => basketId => collateral amount (in MNT, 18 decimals)
    mapping(address => mapping(uint256 => uint256)) public userCollateral;

    /// @notice User debt per basket: user => basketId => debt amount (in basket tokens, 18 decimals)
    mapping(address => mapping(uint256 => uint256)) public userDebt;

    /// @notice Authorized liquidator address (Vincent bot)
    address public liquidator;

    /// @notice Authorized factory that can register basket tokens
    address public authorizedFactory;

    // ============================================================
    // ========================= EVENTS ===========================
    // ============================================================

    event CollateralDeposited(
        address indexed user,
        uint256 indexed basketId,
        uint256 amount,
        uint256 newTotalCollateral
    );

    event CollateralWithdrawn(
        address indexed user,
        uint256 indexed basketId,
        uint256 amount,
        uint256 newTotalCollateral
    );

    event BasketMinted(
        address indexed user,
        uint256 indexed basketId,
        uint256 amount,
        uint256 newTotalDebt
    );

    event BasketBurned(
        address indexed user,
        uint256 indexed basketId,
        uint256 amount,
        uint256 newTotalDebt
    );

    event Liquidated(
        address indexed user,
        uint256 indexed basketId,
        address indexed liquidator,
        uint256 debtRepaid,
        uint256 collateralSeized
    );

    event BasketTokenRegistered(uint256 indexed basketId, address tokenAddress);
    event OracleUpdated(address oldOracle, address newOracle);
    event LiquidatorUpdated(address oldLiquidator, address newLiquidator);
    event FactoryUpdated(address factory);

    // ============================================================
    // ========================= ERRORS ===========================
    // ============================================================

    error InvalidAmount();
    error BasketDoesNotExist();
    error BasketNotActive();
    error InsufficientCollateral();
    error InsufficientDebt();
    error PositionNotLiquidatable();
    error NoDebtToLiquidate();
    error BasketTokenNotRegistered();
    error BasketTokenAlreadyRegistered();
    error ZeroAddress();
    error NotAuthorizedLiquidator();
    error CollateralRatioTooLow();
    error TransferFailed();

    // ============================================================
    // ====================== CONSTRUCTOR =========================
    // ============================================================

    /**
     * @notice Initialize the vault with dependencies
     * @param _basketRegistry Address of the BasketRegistry
     * @param _basketOracle Address of the BasketOracle
     */
    constructor(
        address _basketRegistry,
        address _basketOracle
    ) Ownable(msg.sender) {
        if (_basketRegistry == address(0)) revert ZeroAddress();
        if (_basketOracle == address(0)) revert ZeroAddress();

        basketRegistry = BasketRegistry(_basketRegistry);
        basketOracle = BasketOracle(_basketOracle);
    }

    // ============================================================
    // =================== ADMIN FUNCTIONS ========================
    // ============================================================

    /**
     * @notice Register a basket token for a basket
     */
    function registerBasketToken(uint256 basketId, address tokenAddress) external {
        // Allow owner OR authorized factory to register tokens
        if (msg.sender != owner() && msg.sender != authorizedFactory) {
            revert OwnableUnauthorizedAccount(msg.sender);
        }
        if (!basketRegistry.basketExists(basketId)) revert BasketDoesNotExist();
        if (address(basketTokens[basketId]) != address(0)) revert BasketTokenAlreadyRegistered();
        if (tokenAddress == address(0)) revert ZeroAddress();

        basketTokens[basketId] = EquiBasketToken(tokenAddress);
        emit BasketTokenRegistered(basketId, tokenAddress);
    }

    /**
     * @notice Set the authorized factory address
     * @param _factory Address of the BasketFactory contract
     */
    function setAuthorizedFactory(address _factory) external onlyOwner {
        authorizedFactory = _factory;
        emit FactoryUpdated(_factory);
    }

    function setOracle(address _newOracle) external onlyOwner {
        if (_newOracle == address(0)) revert ZeroAddress();
        address oldOracle = address(basketOracle);
        basketOracle = BasketOracle(_newOracle);
        emit OracleUpdated(oldOracle, _newOracle);
    }

    function setLiquidator(address _liquidator) external onlyOwner {
        address oldLiquidator = liquidator;
        liquidator = _liquidator;
        emit LiquidatorUpdated(oldLiquidator, _liquidator);
    }

    // ============================================================
    // ================= COLLATERAL FUNCTIONS =====================
    // ============================================================

    /**
     * @notice Deposit native MNT collateral for a specific basket
     * @dev Send MNT via msg.value
     * @param basketId The basket to deposit collateral for
     */
    function depositCollateral(uint256 basketId) external payable nonReentrant {
        if (msg.value == 0) revert InvalidAmount();
        if (!basketRegistry.basketExists(basketId)) revert BasketDoesNotExist();

        // Update user's collateral balance for this basket
        userCollateral[msg.sender][basketId] += msg.value;

        emit CollateralDeposited(
            msg.sender,
            basketId,
            msg.value,
            userCollateral[msg.sender][basketId]
        );
    }

    /**
     * @notice Withdraw native MNT collateral from a specific basket
     * @dev Can only withdraw excess collateral that maintains required ratio
     */
    function withdrawCollateral(uint256 basketId, uint256 amount) external nonReentrant {
        if (amount == 0) revert InvalidAmount();
        if (amount > userCollateral[msg.sender][basketId]) revert InsufficientCollateral();

        uint256 newCollateral = userCollateral[msg.sender][basketId] - amount;

        // Check if user has debt - if so, ensure collateral ratio stays above threshold
        uint256 debt = userDebt[msg.sender][basketId];
        if (debt > 0) {
            uint256 newCollateralValue = basketOracle.getMntValue(newCollateral);
            uint256 debtValue = _getDebtValue(basketId, debt);

            uint256 requiredCollateralValue = (debtValue * COLLATERAL_RATIO) / 100;
            if (newCollateralValue < requiredCollateralValue) revert InsufficientCollateral();
        }

        // Update collateral
        userCollateral[msg.sender][basketId] = newCollateral;

        // Transfer native MNT to user
        (bool success, ) = payable(msg.sender).call{value: amount}("");
        if (!success) revert TransferFailed();

        emit CollateralWithdrawn(msg.sender, basketId, amount, newCollateral);
    }

    // ============================================================
    // =================== MINTING FUNCTIONS ======================
    // ============================================================

    /**
     * @notice Mint basket tokens by taking on debt
     * @dev Requires sufficient collateral to maintain COLLATERAL_RATIO
     */
    function mintBasket(uint256 basketId, uint256 amount) external nonReentrant {
        if (amount == 0) revert InvalidAmount();
        if (!basketRegistry.basketExists(basketId)) revert BasketDoesNotExist();
        if (!basketRegistry.isBasketActive(basketId)) revert BasketNotActive();
        if (address(basketTokens[basketId]) == address(0)) revert BasketTokenNotRegistered();

        uint256 newDebt = userDebt[msg.sender][basketId] + amount;
        uint256 newDebtValue = _getDebtValue(basketId, newDebt);
        uint256 requiredCollateralValue = (newDebtValue * COLLATERAL_RATIO) / 100;

        uint256 collateralValue = basketOracle.getMntValue(userCollateral[msg.sender][basketId]);

        if (collateralValue < requiredCollateralValue) revert InsufficientCollateral();

        userDebt[msg.sender][basketId] = newDebt;
        basketTokens[basketId].mint(msg.sender, amount);

        emit BasketMinted(msg.sender, basketId, amount, newDebt);
    }

    /**
     * @notice Burn basket tokens to repay debt and free collateral
     */
    function burnBasket(uint256 basketId, uint256 amount) external nonReentrant {
        if (amount == 0) revert InvalidAmount();
        if (amount > userDebt[msg.sender][basketId]) revert InsufficientDebt();
        if (address(basketTokens[basketId]) == address(0)) revert BasketTokenNotRegistered();

        uint256 currentDebt = userDebt[msg.sender][basketId];
        uint256 currentCollateral = userCollateral[msg.sender][basketId];

        uint256 collateralToRelease = (amount * currentCollateral) / currentDebt;

        userDebt[msg.sender][basketId] -= amount;

        if (userDebt[msg.sender][basketId] == 0) {
            collateralToRelease = currentCollateral;
            userCollateral[msg.sender][basketId] = 0;
        } else {
            userCollateral[msg.sender][basketId] -= collateralToRelease;
        }

        basketTokens[basketId].burn(msg.sender, amount);

        // Transfer native MNT
        (bool success, ) = payable(msg.sender).call{value: collateralToRelease}("");
        if (!success) revert TransferFailed();

        emit BasketBurned(msg.sender, basketId, amount, userDebt[msg.sender][basketId]);
    }

    // ============================================================
    // ================= LIQUIDATION FUNCTIONS ====================
    // ============================================================

    function isLiquidatable(address user, uint256 basketId) public view returns (bool) {
        uint256 debt = userDebt[user][basketId];
        if (debt == 0) return false;

        uint256 ratio = getCollateralRatio(user, basketId);
        return ratio < (LIQUIDATION_THRESHOLD * PRECISION / 100);
    }

    function liquidate(address user, uint256 basketId) external payable nonReentrant {
        if (!isLiquidatable(user, basketId)) revert PositionNotLiquidatable();

        uint256 debt = userDebt[user][basketId];
        if (debt == 0) revert NoDebtToLiquidate();
        if (address(basketTokens[basketId]) == address(0)) revert BasketTokenNotRegistered();

        uint256 debtValue = _getDebtValue(basketId, debt);
        uint256 collateralToSeize = userCollateral[user][basketId];

        // Convert debt value to MNT that liquidator needs to pay
        uint256 mntToPayForDebt = basketOracle.getMntFromUsdValue(debtValue);

        // Require liquidator sends enough MNT
        if (msg.value < mntToPayForDebt) revert InvalidAmount();

        // Clear user's position
        userCollateral[user][basketId] = 0;
        userDebt[user][basketId] = 0;

        // Transfer seized collateral to liquidator
        (bool success, ) = payable(msg.sender).call{value: collateralToSeize}("");
        if (!success) revert TransferFailed();

        // Refund excess MNT
        if (msg.value > mntToPayForDebt) {
            (success, ) = payable(msg.sender).call{value: msg.value - mntToPayForDebt}("");
            if (!success) revert TransferFailed();
        }

        emit Liquidated(user, basketId, msg.sender, debt, collateralToSeize);
    }

    // ============================================================
    // ===================== VIEW FUNCTIONS =======================
    // ============================================================

    function getCollateralRatio(address user, uint256 basketId) public view returns (uint256) {
        uint256 debt = userDebt[user][basketId];
        if (debt == 0) return type(uint256).max;

        uint256 collateralValue = basketOracle.getMntValue(userCollateral[user][basketId]);
        uint256 debtValue = _getDebtValue(basketId, debt);

        if (debtValue == 0) return type(uint256).max;

        return (collateralValue * PRECISION) / debtValue;
    }

    function getMaxMintable(address user, uint256 basketId) external view returns (uint256) {
        uint256 collateralValue = basketOracle.getMntValue(userCollateral[user][basketId]);
        uint256 currentDebtValue = _getDebtValue(basketId, userDebt[user][basketId]);

        uint256 maxDebtValue = (collateralValue * 100) / COLLATERAL_RATIO;

        if (maxDebtValue <= currentDebtValue) return 0;

        uint256 availableDebtValue = maxDebtValue - currentDebtValue;

        uint256 basketPrice = basketOracle.getBasketPrice(basketId);
        if (basketPrice == 0) return 0;

        return (availableDebtValue * PRECISION) / basketPrice;
    }

    struct UserPositionView {
        uint256 collateral;
        uint256 debt;
        uint256 collateralRatio;
        bool liquidatable;
    }

    function getUserPosition(address user, uint256 basketId) external view returns (UserPositionView memory) {
        return UserPositionView({
            collateral: userCollateral[user][basketId],
            debt: userDebt[user][basketId],
            collateralRatio: getCollateralRatio(user, basketId),
            liquidatable: isLiquidatable(user, basketId)
        });
    }

    // ============================================================
    // ================= INTERNAL FUNCTIONS =======================
    // ============================================================

    function _getDebtValue(uint256 basketId, uint256 debtAmount) internal view returns (uint256) {
        if (debtAmount == 0) return 0;
        uint256 basketPrice = basketOracle.getBasketPrice(basketId);
        return (debtAmount * basketPrice) / PRECISION;
    }

    // Allow receiving native MNT
    receive() external payable {}
}

contracts/EquiBasketToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title EquiBasketToken
 * @notice ERC20 token representing ownership of a basket-based synthetic asset
 * @dev Each basket has its own EquiBasketToken deployed when the basket is created.
 *      Unlike single-asset tokens, this token:
 *      - Stores the basketId it represents
 *      - Has name and symbol derived from basket metadata
 *      - Is only mintable/burnable by the authorized vault
 * 
 *      The token is a 1:1 representation of debt owed for a basket.
 *      When a user mints basket tokens, they take on a debt position.
 *      When they burn, they repay that debt.
 */
contract EquiBasketToken is ERC20, Ownable {
    // ============================================================
    // ========================== STATE ===========================
    // ============================================================

    /// @notice The basket ID this token represents
    uint256 public immutable basketId;

    /// @notice Address of the vault authorized to mint/burn
    address public vault;

    // ============================================================
    // ========================= EVENTS ===========================
    // ============================================================

    /// @notice Emitted when the vault address is updated
    event VaultUpdated(address indexed oldVault, address indexed newVault);

    // ============================================================
    // ========================= ERRORS ===========================
    // ============================================================

    /// @notice Thrown when caller is not the vault
    error OnlyVault();

    /// @notice Thrown when vault address is zero
    error ZeroVaultAddress();

    // ============================================================
    // ======================== MODIFIERS =========================
    // ============================================================

    /**
     * @notice Ensures caller is the authorized vault
     */
    modifier onlyVault() {
        if (msg.sender != vault) revert OnlyVault();
        _;
    }

    // ============================================================
    // ====================== CONSTRUCTOR =========================
    // ============================================================

    /**
     * @notice Deploy a new basket token
     * @param _basketId The basket ID this token represents
     * @param _name Token name (e.g., "EquiBasket Tech Giants")
     * @param _symbol Token symbol (e.g., "eTECH")
     * @param _owner The owner of this token contract (usually the deployer/factory)
     */
    constructor(
        uint256 _basketId,
        string memory _name,
        string memory _symbol,
        address _owner
    ) ERC20(_name, _symbol) Ownable(_owner) {
        basketId = _basketId;
    }

    // ============================================================
    // =================== ADMIN FUNCTIONS ========================
    // ============================================================

    /**
     * @notice Set the vault address that can mint/burn tokens
     * @dev Only callable by owner. This links the token to its vault.
     * @param _vault Address of the BasketVault contract
     */
    function setVault(address _vault) external onlyOwner {
        if (_vault == address(0)) revert ZeroVaultAddress();

        address oldVault = vault;
        vault = _vault;

        emit VaultUpdated(oldVault, _vault);
    }

    // ============================================================
    // ================== VAULT-ONLY FUNCTIONS ====================
    // ============================================================

    /**
     * @notice Mint tokens to user when they take a debt position
     * @dev Only callable by the vault during minting operations
     * @param user The user receiving the tokens
     * @param amount Amount of tokens to mint (in 18 decimals)
     */
    function mint(address user, uint256 amount) external onlyVault {
        _mint(user, amount);
    }

    /**
     * @notice Burn tokens from user when they repay debt
     * @dev Only callable by the vault during burning operations
     * @param user The user whose tokens are burned
     * @param amount Amount of tokens to burn (in 18 decimals)
     */
    function burn(address user, uint256 amount) external onlyVault {
        _burn(user, amount);
    }
}
